import axios from "axios";
import { useEffect, useState } from "react";
import CustomPagination from "../../components/Pagination/CustomPagination";
import SingleContent from "../../components/SingleContent/SingleContent";

const Movies = () => {
  const [page,setPage] = useState(1);
  const [content,setContent]= useState([]);
  const [numOfPages,setNumOfPages]=useState();
  const [selectedGenres,setSelectedGames] = useState([]);
  const [genres,setGenres] = useState([]);

  const fetchMovies = async()=> {
    const {data}=await axios.get(`https://api.themoviedb.org/3/discover/movie?api_key=74f5938285ff4cb9d571ebcf8646ab9c&language=telugu&sort_by=popularity.desc&include_adult=false&include_video=false&page=1&with_watch_monetization_types=flatrate&page=${page}`);
    setContent(data.results);
    setNumOfPages(data.total_pages);
  }
  useEffect(()=>{
    fetchMovies();
  },[page]);
  return (
    <div>
      <span className="pageTitle">Movies</span>
      <Genres 
        type='movie' 
        selectedGenres={selectedGenres} 
        setSelectedGenres={setSelectedGenres}
        genres={genres} setGenres={setGenres}></Genres>
      <div className="trending">
          {
            content && content.map((c) => (
              <SingleContent 
              key={c.id} id={c.id} 
              poster={c.poster_path} 
              title={c.title || c.name} 
              date={c.first_air_date || c.release_date}
              media_type= "movie"
              vote_average={c.vote_average}
              />
              ))
          }
        </div> 
          {numOfPages>1&& (
            <CustomPagination setPage={setPage} numOfPages={500}/>
          )}
          
    </div>
  )
}

export default Movies
